void selection_sort(int *v, int n)
{
    int i, j, menor, troca;
    for(i = 0; i < n -1; i++)
    {
        menor = i;

        for(j = i + i; j < n; j++){
            if(v[j] < v[menor])
                menor = j;
        }

        if( i != menor){
            troca = v[i];
            v[i] = v[menor];
            v[menor] = troca;
        }
    }
}

void printVetor(int *v, int n)
{
    for (int i = 0; i < n; i++)
    {
        printf("%d ", v[i]);
    }
    printf("\n");
}

